import React, {Component} from 'react';
import {PropTypes} from 'prop-types';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import { addMeal } from '../action/meals';

class Bookings extends Component {
  constructor(props){
    super(props)
    this.state={
       usernames:"",
       dates:"",
       guestList:[],
       dateRange:[]
    }
  }
  handleGuestInfo = (event) => {

    let guestName = event.target.value;
    if(guestName){
    this.setState(prevState => {
      prevState.usernames = guestName;

    });
  }

  }
  handleDateInfo = (event) => {

    let dateRange = event.target.value;
    // dateRange = dateRange.split("\n");
    this.setState(prevState => {
      prevState.dates = dateRange;

    });

  }

  listMeals = () => {
    
    const guests = this.state.usernames.split("\n");
    const dates = this.state.dates.split("\n");
    this.setState(prevState => {
      prevState.guestList = [...prevState.guestList,...guests];
      prevState.dateRange = [...prevState.dateRange,...dates];

    });

    this.props.dispatch(addMeal(guests,dates));



  }

    render() {
        return (
      <div className="row">
        <TextField
          className="col-md-6"
          multiline
          rows="4"
          placeholder="Enter the names (one name per line)"
          onChange={this.handleGuestInfo}
        />
        <TextField
          className="col-md-6"
          multiline
          rows="4"
          placeholder="Enter the date range for each name (one range per line)"
          onChange={this.handleDateInfo}
        />
        <Button variant="outlined" color="primary" className="block-center" onClick={this.listMeals}>Get Meals Schedule</Button>
        </div>);
    }
}



export default connect(undefined,mapDispatchToProps)(Bookings);
