const mealsSchedule ={
  guest:[],
  startDates:[],
  endDates:[]
}

const mealReducer = (state = mealsSchedule,action)=>{
  switch(action.type){

    case "GET_MEAL_SCHEDULE":
      return {
        guest:[...state.guest,...action.info.guests],
        startDates: [...state.startDates,...action.info.startDate],
        endDates: [...state.endDates,...action.info.endDate]
      }

    default:
     return state;

  }
}

export default mealReducer;
