import React, {Component} from 'react';
import {PropTypes} from 'prop-types';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class Bookings extends Component {
  constructor(props){
    super(props)
    this.state={
       usernames:[],
       dates:[]
    }
  }

  
  
  handleGuestInfo = (event) => {

    let guestName = event.target.value;
    guestName = guestName.split("\n");
    this.setState(prevState => {
      prevState.usernames = [...prevState.usernames, ...guestName];

    })

  }
  handleDateInfo = (event) => {

    let dateRange = event.target.value;
    dateRange = dateRange.split("\n");
    this.setState(prevState => {
      prevState.dates = [...prevState.dates, ...dateRange];

    })

  }

    render() {
        return (
      <div className="row">
        <TextField
          className="col-md-6"
          multiline
          rows="4"
          placeholder="Enter the names (one name per line)"
          onChange={this.handleGuestInfo}
        />
        <TextField
          className="col-md-6"
          multiline
          rows="4"
          placeholder="Enter the date range for each name (one range per line)"
          onChange={this.handleDateInfo}
        />
        <Button variant="outlined" color="primary" className="block-center">Get Meals Schedule</Button>
        </div>);
    }
}

export default Bookings;
