const mealsSchedule ={
  guest:[],
  dates:[]
}

const mealReducer = (state = mealsSchedule,action)=>{
  switch(action.type){

    case "GET_MEAL_SCHEDULE":
      return {
        guest:[...state.guest,...action.guest],
        dates: [...state.dates,...action.guest]
      }

    default:
     return state;

  }
}

export default mealReducer;
